import React, { useEffect, useState } from 'react';
import Layout from './Layout';
import LeagueService from './../services/LeagueService';
import { format } from 'date-fns';

function Schedule() {
    const [matches, setMatches] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const apiUrl = 'http://localhost:3001/api/v1';
            const token = 'YuHBdSlDXY000xa8IlCm7Qgq4_s';
            const leagueService = new LeagueService(apiUrl, token);

            try {
                const matchesData = await leagueService.getMatches();
                setMatches(matchesData);
            } catch (error) {
                console.error('Error fetching matches:', error);
            }
        };

        fetchData();
    }, []);

    return (
        <Layout>
            <h1 className="text-gray-600 text-2xl font-bold mb-4 text-center">League Schedule</h1>

            <table className="w-full mx-auto divide-y divide-gray-200">
                <thead className="bg-gray-50">
                    <tr>
                        <th className="px-6 py-3 text-left text-sm font-bold text-gray-400 w-1/6">Date/Time</th>
                        <th className="px-6 py-3 text-left text-sm font-bold text-gray-400 w-1/2">Stadium</th>
                        <th className="px-6 py-3 text-left text-sm font-bold text-gray-400 w-1/2">Home Team</th>
                        <th className="px-6 py-3 text-left text-sm font-bold text-gray-400 w-1/2">Away Team</th>
                    </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                    {matches.map((match, index) => (
                        <tr key={index} className={index % 2 === 0 ? 'bg-gray-50' : 'bg-white'}>
                            <td className="px-6 py-4 whitespace-nowrap text-gray-300">{format(new Date(match.matchDate), 'd.M.yyyy HH:mm')}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-gray-300">{match.stadium}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-gray-300">{match.homeTeam}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-gray-300">{match.awayTeam}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </Layout>
    );
}

export default Schedule;
