import React, { useEffect, useState } from 'react';
import LeagueService from './../services/LeagueService';

function Leaderboard() {
  const [leaderboard, setLeaderboard] = useState([]);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const apiUrl = 'http://localhost:3001/api/v1';
        const token = 'YuHBdSlDXY000xa8IlCm7Qgq4_s';
        const leagueService = new LeagueService(apiUrl, token);
        const leaderboardData = await leagueService.getLeaderboard();
        setLeaderboard(leaderboardData);
      } catch (error) {
        console.error('Error fetching leaderboard:', error);
      }
    };

    fetchLeaderboard();
  }, []);

  return (
    <div className="container mx-auto">
      <h1 className="text-2xl font-bold my-4">Leaderboard</h1>
      <table className="w-full">
        <thead>
          <tr className="bg-gray-200">
            <th className="py-2 px-4">Team</th>
            <th className="py-2 px-4">Matches Played</th>
            <th className="py-2 px-4">Goals For</th>
            <th className="py-2 px-4">Goals Against</th>
            <th className="py-2 px-4">Points</th>
          </tr>
        </thead>
        <tbody>
          {leaderboard.map((team, index) => (
            <tr key={index} className="bg-gray-100">
              <td className="py-2 px-4">{team.teamName}</td>
              <td className="py-2 px-4">{team.matchesPlayed}</td>
              <td className="py-2 px-4">{team.goalsFor}</td>
              <td className="py-2 px-4">{team.goalsAgainst}</td>
              <td className="py-2 px-4">{team.points}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Leaderboard;
